// 1) Crie um array apenas com os dados JSON
const allData = items.map(item => item.json);

// 2) Ordene pelo campo "id" (por exemplo)
allData.sort((a, b) => a.id - b.id);

// 3) Retorne cada objeto como um item separado
return allData.map(entry => ({
  json: entry
}));

return $items("Set File ID").map(item => {
  return {
    json: {
      file_id: item.json.file_id,
      file_type: item.json.file_type,
      file_name: item.json.file_name
    }
  };
});
