// Classifica√ß√£o inteligente de severidade
const errorDesc = ($json.error_description || '').toLowerCase();
const errorType = ($json.error_type || '').toLowerCase();
const nodeName = ($json.node_name || '').toLowerCase();

let severity = 'low';
let priority = 1;

// Palavras-chave para alta severidade
const highSeverityKeywords = [
  'fatal', 'critical', 'timeout', 'connection refused', 
  'database', 'authentication', 'authorization', 'payment',
  'security', 'crash', 'memory', 'disk space'
];

// Palavras-chave para m√©dia severidade
const mediumSeverityKeywords = [
  'retry', 'warning', 'deprecated', 'limit exceeded',
  'rate limit', 'validation', 'temporary', 'network'
];

// N√≥s cr√≠ticos que sempre geram alta severidade
const criticalNodes = [
  'webhook', 'database', 'payment', 'auth', 'api gateway'
];

// Verificar severidade baseada em m√∫ltiplos fatores
if (highSeverityKeywords.some(keyword => 
    errorDesc.includes(keyword) || errorType.includes(keyword)) ||
    criticalNodes.some(node => nodeName.includes(node))) {
  severity = 'high';
  priority = 3;
} else if (mediumSeverityKeywords.some(keyword => 
    errorDesc.includes(keyword) || errorType.includes(keyword))) {
  severity = 'medium';
  priority = 2;
}

// Adicionar contexto adicional
const errorContext = {
  severity,
  priority,
  is_recurring: false, // Ser√° definido pelo throttling
  error_hash: require('crypto').createHash('md5')
    .update(`${$json.workflow_id}-${$json.node_name}-${errorType}`)
    .digest('hex').substring(0, 8)
};

return [{ json: { ...$json, ...errorContext } }];

// Throttling inteligente para LOW severity
const staticData = $getWorkflowStaticData('node');
const now = Date.now();
const errorHash = $json.error_hash;
const throttleKey = `low_${errorHash}`;

// Inicializar estrutura se n√£o existir
if (!staticData.throttle) {
  staticData.throttle = {};
}

const lastOccurrence = staticData.throttle[throttleKey] || 0;
const hourlyThreshold = 60 * 60 * 1000; // 1 hora

// Se o mesmo erro ocorreu h√° menos de 1 hora, throttle
if (now - lastOccurrence < hourlyThreshold) {
  // Incrementar contador de ocorr√™ncias
  const countKey = `count_${errorHash}`;
  staticData.throttle[countKey] = (staticData.throttle[countKey] || 0) + 1;
  return []; // Bloquear notifica√ß√£o
}

// Atualizar timestamp e resetar contador
staticData.throttle[throttleKey] = now;
staticData.throttle[`count_${errorHash}`] = 1;

// Adicionar informa√ß√£o de recorr√™ncia
const updatedItem = {
  ...items[0].json,
  occurrence_count: staticData.throttle[`count_${errorHash}`]
};

return [{ json: updatedItem }];

// Throttling inteligente para MEDIUM severity
const staticData = $getWorkflowStaticData('node');
const now = Date.now();
const errorHash = $json.error_hash;
const throttleKey = `medium_${errorHash}`;

// Inicializar estrutura se n√£o existir
if (!staticData.throttle) {
  staticData.throttle = {};
}

const lastOccurrence = staticData.throttle[throttleKey] || 0;
const throttleThreshold = 15 * 60 * 1000; // 15 minutos

// Se o mesmo erro ocorreu h√° menos de 15 minutos, throttle
if (now - lastOccurrence < throttleThreshold) {
  const countKey = `count_${errorHash}`;
  staticData.throttle[countKey] = (staticData.throttle[countKey] || 0) + 1;
  return [];
}

// Atualizar timestamp
staticData.throttle[throttleKey] = now;
staticData.throttle[`count_${errorHash}`] = 1;

const updatedItem = {
  ...items[0].json,
  occurrence_count: staticData.throttle[`count_${errorHash}`]
};

return [{ json: updatedItem }];

// Throttling m√≠nimo para HIGH severity (apenas para evitar spam)
const staticData = $getWorkflowStaticData('node');
const now = Date.now();
const errorHash = $json.error_hash;
const throttleKey = `high_${errorHash}`;

// Inicializar estrutura se n√£o existir
if (!staticData.throttle) {
  staticData.throttle = {};
}

const lastOccurrence = staticData.throttle[throttleKey] || 0;
const throttleThreshold = 2 * 60 * 1000; // 2 minutos apenas para evitar spam

// Throttle m√≠nimo apenas para o mesmo erro exato
if (now - lastOccurrence < throttleThreshold) {
  const countKey = `count_${errorHash}`;
  staticData.throttle[countKey] = (staticData.throttle[countKey] || 0) + 1;
  // Para erros HIGH, ainda notifica mas marca como recorrente
  const updatedItem = {
    ...items[0].json,
    is_recurring: true,
    occurrence_count: staticData.throttle[`count_${errorHash}`]
  };
  return [{ json: updatedItem }];
}

// Atualizar timestamp
staticData.throttle[throttleKey] = now;
staticData.throttle[`count_${errorHash}`] = 1;

const updatedItem = {
  ...items[0].json,
  occurrence_count: staticData.throttle[`count_${errorHash}`]
};

return [{ json: updatedItem }];

